
def frequency_analysis(message, length):
    """
    Counts how many n-length sequences are in the text"""
    freq_map = {}
    for i in range(0, len(message), length):
        try:
            seq = message[i:i+length]
        except:
            seq = message[i:]
        freq_map[seq] = freq_map.get(seq, 0) + 1
    return freq_map

message = ""

print(frequency_analysis(message, 4))
        